import React, { useState, createContext, useMemo } from 'react';
import PropTypes from 'prop-types';

export const AuthenticatedUserContext = createContext({});

export function AuthenticatedUserProvider({ children }) {
  const [user, setUser] = useState(null);
  const userMemo = useMemo(() => ({ user, setUser }), []);
  return (
    <AuthenticatedUserContext.Provider value={userMemo}>
      {children}
    </AuthenticatedUserContext.Provider>
  );
}

AuthenticatedUserProvider.prototype = {
  children: PropTypes.string.isRequired,
};

AuthenticatedUserProvider.propTypes = {
  children: PropTypes.string.isRequired,
};
